// HasFlaggedOrders applies the HasEdge predicate on the "flagged_orders" edge.
func HasFlaggedOrders() predicate.OrderFlag {
	return predicate.OrderFlag(func(s *sql.Selector) {
		step := sqlgraph.NewStep(
			sqlgraph.From(Table, FieldID),
			sqlgraph.Edge(sqlgraph.M2M, false, FlaggedOrdersTable, FlaggedOrdersPrimaryKey...),
		)
		sqlgraph.HasNeighbors(s, step)
	})
}

// HasFlaggedOrdersWith applies the HasEdge predicate on the "flagged_orders" edge with a given conditions (other predicates).
func HasFlaggedOrdersWith(preds ...predicate.OrderInfo) predicate.OrderFlag {
	return predicate.OrderFlag(func(s *sql.Selector) {
		step := newFlaggedOrdersStep()
		sqlgraph.HasNeighborsWith(s, step, func(s *sql.Selector) {
			for _, p := range preds {
				p(s)
			}
		})
	})
}

