func (sq *SampleQuery) loadSampleRequiredTubes(ctx context.Context, query *TubeRequirementQuery, nodes []*Sample, init func(*Sample), assign func(*Sample, *TubeRequirement)) error {
	fks := make([]driver.Value, 0, len(nodes))
	nodeids := make(map[int]*Sample)
	for i := range nodes {
		fks = append(fks, nodes[i].ID)
		nodeids[nodes[i].ID] = nodes[i]
		if init != nil {
			init(nodes[i])
		}
	}
	if len(query.ctx.Fields) > 0 {
		query.ctx.AppendFieldOnce(tuberequirement.FieldSampleID)
	}
	query.Where(predicate.TubeRequirement(func(s *sql.Selector) {
		s.Where(sql.InValues(s.C(sample.SampleRequiredTubesColumn), fks...))
	}))
	neighbors, err := query.All(ctx)
	if err != nil {
		return err
	}
	for _, n := range neighbors {
		fk := n.SampleID
		node, ok := nodeids[fk]
		if !ok {
			return fmt.Errorf(`unexpected referenced foreign-key "sample_id" returned %v for node %v`, fk, n.ID)
		}
		assign(node, n)
	}
	return nil
}
